// Receiving the custom event
twiki.addEventListener('PAGE_CHANGE', function(event) {
    // Access the event detail
    const page = event.detail;
    const head = page.head;
    //if head <meta name="ajs-page-id"  get the content value
    try {
        let page_id = head.match(/<meta name="ajs-page-id" content="(.*)">/)[1]
    } catch (error) {
        console.log('No page id found')
        return
    }
    
    let page_title = head.match(/<title>(.*)<\/title>/)[1]
    if (page_id) {
        chatHeader.setAction('Back')
        chatHeader.updateTitle(page_title)
        twiki.updateContent(page.body, page_id);
        horizontalScrollPanels.switchByID('twiki')
    }
    console.log('Is Wiki:', isWiki);

    selection.innerHTML = page.body;
    
    // Handle the event (e.g., update the UI, log the page, etc.)
    // console.log('Page changed:', page);
    // You can add more logic here to handle the page change
});


